package session2;

public class _3_ElseIfStatment {
    public static void main(String[] args) {
        int x = 10;
        int y = 12;
        if(x+y < 10) {
            System.out.println("x + y is less than      10");
        }
        else if(x+y <15){
            System.out.println("x + y is less than      15");
        }
        else {
            System.out.println("x + y is greater than 20");
        }
    }

}

